import React, { useState } from "react";
import ReactDOM from "react-dom";

const App = () => {
  // save clicks of each button to own state
  const [good, setGood] = useState(0);
  const [neutral, setNeutral] = useState(0);
  const [bad, setBad] = useState(0);

  const handleGood = () => {
    setGood(good + 1);
  };
  const handleNeutral = () => {
    setNeutral(neutral + 1);
  };
  const handleBad = () => {
    setBad(bad + 1);
  };

  const Button = (props) => {
    return <button onClick={props.clickHandler}>{props.text}</button>;
  };

  const Statistics = (props) => {
    const sum = good + neutral + bad;
    const average = (good - bad) / sum;
    const positive = good / sum;
    if (sum == 0) {
      return (
        <div>
          <h1>Statistics</h1>
          <p>No feedback given</p>
        </div>
      );
    } else {
      return (
        <div>
          <h1>Statistics</h1>
          <p>good {good}</p>
          <p>neutral {neutral}</p>
          <p>bad {bad}</p>
          <p>all {sum}</p>
          <p>average {average}</p>
          <p>positive {positive}%</p>
        </div>
      );
    }
  };
  return (
    <div>
      <h1>give feedback</h1>
      <Button text="good" clickHandler={handleGood} />

      <Button text="neutral" clickHandler={handleNeutral} />

      <Button text="bad" clickHander={handleBad} />

      <Statistics />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById("root"));
